<!DOCTYPE><html>	<head>	  <meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1">	  <title>Ahl's Cake</title>	  <script type="text/javascript" src="../../js/script.js"></script>	  <script type="text/javascript" src="../../js/jquery.min.js"></script>	  <script src="../../js/jquery.toastmessage.js" type="text/javascript"></script>	  <link href="../../css/jquery.toastmessage.css" rel="stylesheet" type="text/css">	  <link href="../../css/style.css" rel="stylesheet" type="text/css">	</head>	<?php 	    //connect		$connect = pg_connect("host=localhost port=5432 dbname=ahlscake user=postgres password=admin");		if (!$connect) {			die("Error in connection: " . pg_last_error());		}				$offset = $_REQUEST['offset']; 		$pgnum = $_REQUEST['pgnum'];				$limit=5; // rows to return		$rowcount = pg_query($connect, "SELECT * FROM product");		$numrows=pg_num_rows($rowcount);		?>	<body>		<div class="wrapper">			<div class="main">				<div class="header">Ahl's Cakes</div>				<div class="mid">					<div class="mid-left">						<h2 class="gap-2">MENU</h2>						<ul class="left-nav bmenu">						  <li class="top"><a href="../../">Home</a></li>						  <li><a href="../customer_view_products/?offset=0&pgnum=0">View Products</a></li>						  <li><a href="../bulk_order/">Order Online</a></li>						  <li><a href="../comments/">Comment</a></li>						  <li><a href="#">Contact</a></li>						  <li class="bottom"><a href="#">About Us</a></li>						</ul>						<?php						// navigation							session_start();							$top=0;							if(!isset($_SESSION["role"])){								echo "<h2 class=\"gap-2\">LOGIN</h2>									<ul class=\"left-nav bmenu\">									<li class='login top'>";								include("../login/index.php");								echo "<li class='bottom'><a href=\"../sign_up/\">Sign Up</a></li>";								echo "</li>									</ul>";							}							else{								$role=strtoupper($_SESSION["role"]); 								echo "<h2 class=\"gap-2\">{$role}</h2>								<ul class=\"left-nav bmenu\">";								if($_SESSION["role"]=="owner"){									echo "<li class='top'><a href=\"../add_employee/\">Add Employee</a></li>										  <li><a href='../delete_employee/'>Delete Employee</a></li>										  <li><a href='../search_employee/'>Search Employee</a></li>										  <li><a href='../view_all_employee/'>View All Employee</a></li>";									$top=1;								}								if($_SESSION["role"]=="employee" || $_SESSION["role"]=="owner"){										if($top==1) echo "<li><a href='../cashier_system/'>Cashier System</a></li>";										else echo "<li class='top'><a href='../cashier_system/'>Cashier System</a></li>";										echo "<li><a href='../inventory_system/'>Inventory System</a></li>											  <li><a href='../income_reports/'>Income Reports</a></li>											  <li><a href='../income_graphs/'>Income Graphs</a></li>											  <li><a href='../add_product/'>Add Product</a></li>											  <li><a href='../delete_product/'>Delete Product</a></li>											  <li class='bottom'><a href=\"../logout/index.php\">Logout</a></li>";								}							}						?>					</div>					<div class="mid-right">						<h1 class="gap-1">View All Product</h1>													<?php if ($offset==0){?>								  <table class="bestsellers">							<?php									    $i=0;									$result1 = pg_query($connect, "SELECT prod_id FROM cashier order by prod_quantity DESC");									echo "<h3>Best sellers</h3><br>";									echo "<tr class='TDR'>";									while (($row = pg_fetch_row($result1)) && $i<3) {										$result = pg_query($connect, "SELECT * FROM product where prod_id=$row[0]");										$row = pg_fetch_row($result);										echo "<td><img id='{$row[1]}' src='../../products/$row[1].jpg' width='200' height='200' onerror='imgError(\"$row[1]\");'/><br><center>$row[1]</center></td>" ;										$i++;									} 									echo "</tr>";																?>								</table>								<br><br><br><br>							<?php } ?>																<h3>Displaying all products</h3><br>								<table class="viewAllTable">								<tr>									<td class="deleteId">Image</td>									<td class="deleteId">Information</td>								</tr><?php									$result2 = pg_query($connect, "SELECT * FROM product limit $limit offset $offset");									while ($row = pg_fetch_row($result2)) {										echo "<tr class='TDR'>";										echo "<td class='TDR'><img id='{$row[1]}' src='../../products/$row[1].jpg' width='200' height='200' onerror='imgError(\"$row[1]\");'/></td> <td class='TDR description'> <b class='green'>Name:</b> $row[1] <br> <b class='green'>Price:</b> $row[4] <br> <b class='green'>Description:</b> $row[2]</td>" ;										if(isset($_SESSION["role"])){											echo "<td class='TDR'>											  <form name='myForm{$i}' method='post' action='../edit_product/index.php'> 												<input type='hidden' name='id' value='{$row[0]}'/>												<input type='submit' name='edit' id='submit' value='Edit'/>											  </form>											  </td>";											echo "</tr>";										}									}									?></table><?php									$pages=intval($numrows/$limit);									if ($numrows%$limit) $pages++;                                     ?><br>									<ul id="pagination"><?php									if ($pages!=1) { 										if ($pgnum==1) 											print "<li><a href=\"index.php?offset=0&pgnum=1\">PREV</a></li>"; 										else { 											$prevoffset=$offset-5; 											$cpgnum = intval($prevoffset/$limit)+1; 											print "<li><a href=\"index.php?offset=$prevoffset&pgnum=$cpgnum\">PREV</a></li>"; 										} 									} 																		for ($i=1;$i<=$pages;$i++) { // loop thru 										$newoffset=$limit*($i-1); 										$cpgnum = $i; 										print "<li><a href=\"index.php?offset=$newoffset&pgnum=$cpgnum\">$cpgnum</a></li>"; 									} 															if ($pages!=1){ 										if ($pgnum<$pages) { 											$newoffset=$offset+$limit; 											$cpgnum = intval(($offset+$limit)/$limit)+1; 											print "<li><a href=\"index.php?offset=$newoffset&pgnum=$cpgnum\">NEXT</a></li>";										} 										else{ 											print "<li><a href=\"index.php?offset=$newoffset&pgnum=$pages\">NEXT</a></li>";										} 									} 									pg_close($connect); 									?> </ul>					</div>				</div>				<div class="footer">					<p class="copyright">© COPYRIGHT 2013 ALL RIGHTS RESERVED</p>				</div>			</div>		</div>	</body></html>